\begin{equation*}
  \begin{aligned}
   f(x)=&\frac{2+(x+3)\sqrt{-x}}{2(x+4)}(\sqrt{-x})^n+\frac{2-(x+3)\sqrt{-x}}{2(x+4)}(-\sqrt{-x})^n \\
   &+\frac{x+2+\sqrt{x(x+4)}}{2(x+4)}\left ( \frac{x+\sqrt{x(x+4)}}{2} \right )^n+\frac{x+2-\sqrt{x(x+4)}}{2(x+4)}\left ( \frac{x-\sqrt{x(x+4)}}{2} \right )^n.
  \end{aligned}
  \end{equation*}
